(158, 'Cử tri sinh viên cảnh sát lần đầu bỏ phiếu bầu cử', 'Những cử tri là sinh viên lần đầu cầm tấm thẻ cử tri đi bầu đều có tâm trạng phấn khởi, náo nức.<br /><img src="http://imgs.vietnamnet.vn/Images/2016/05/22/11/20160522113545-anh-7.jpg?w=220" />', 'http://vietnamnet.vn/vn/giao-duc/306136/cu-tri-sinh-vien-canh-sat-lan-dau-bo-phieu-bau-cu.html', '22/05/2016 (GMT+7)', 'http://imgs.vietnamnet.vn/Images/2016/05/22/11/20160522113545-anh-7.jpg', '', '', 1463889179, NULL),
(159, 'Dân chung cư ''đắp đập be bờ'' cứu hàng trăm xế hộp', 'Cơn mưa lớn vào chiều tối qua đến sáng nay đã khiến đường phố, nhiều khu dân cư ở Hà Nội ngập sâu trong nước.<br /><img src="http://imgs.vietnamnet.vn/Images/2016/05/25/09/20160525095258-img-2724.JPG?w=220" />', 'http://vietnamnet.vn/vn/bat-dong-san/306609/dan-chung-cu-dap-dap-be-bo-cuu-hang-tram-xe-hop.html', '25/05/2016 (GMT+7)', 'http://imgs.vietnamnet.vn/Images/2016/05/25/09/20160525095258-img-2724.JPG', '', 'http://vietnamnet.vn/vn/bat-dong-san/306609/dan-chung-cu-dap-dap-be-bo-cuu-hang-tram-xe-hop.html', 1464170485, NULL),
(160, 'Hà Nội: Sẽ hoàn thành đường “cong mềm mại” trong năm nay', 'Hiện, công tác GPMB vẫn đang được tiến hành gấp rút để có thể hoàn thành công trình trong năm 2016.<br /><img src="http://imgs.vietnamnet.vn/Images/2016/05/25/10/20160525103351-duong-truong-chinh2.jpg?w=220" />', 'http://vietnamnet.vn/vn/bat-dong-san/du-an/306630/ha-noi-se-hoan-thanh-duong-cong-mem-mai-trong-nam-nay.html', '25/05/2016 (GMT+7)', 'http://imgs.vietnamnet.vn/Images/2016/05/25/10/20160525103351-duong-truong-chinh2.jpg', '', 'http://vietnamnet.vn/vn/bat-dong-san/du-an/306630/ha-noi-se-hoan-thanh-duong-cong-mem-mai-trong-nam-nay.html', 1464170485, NULL),
(161, 'Kiến nghị triển khai tiếp gói 30.000 tỷ', 'Hiệp Hội Bất động sản Hồ Chí Minh (HoREA) vừa có công văn gửi Văn phòng Chính phủ, Ngân hàng Nhà nước (NHNN) về việc giải ngân gói tín dụng 30.000 tỷ đồng sau ngày 1/6.<br /><img src="http://imgs.vietnamnet.vn/Images/2016/05/24/16/20160524164028-goi-30-nghin-ty.jpg?w=220" />', 'http://vietnamnet.vn/vn/bat-dong-san/thi-truong/306525/kien-nghi-trien-khai-tiep-goi-30-000-ty.html', '25/05/2016 (GMT+7)', 'http://imgs.vietnamnet.vn/Images/2016/05/24/16/20160524164028-goi-30-nghin-ty.jpg', '', 'http://vietnamnet.vn/vn/bat-dong-san/thi-truong/306525/kien-nghi-trien-khai-tiep-goi-30-000-ty.html', 1464170485, NULL),
(162, 'Những căn hộ chung cư đẹp hút hồn nhờ ban công hoa', 'Tận dụng khoảng không gian nhỏ hẹp ở ban công, rất nhiều gia đình sống ở những căn hộ chung cư đã vô cùng khéo léo tạo nên những khu vườn ngát hương hoa trên ban công nhỏ.<br /><img src="http://imgs.vietnamnet.vn/Images/2016/05/25/14/20160525145810-ban-cong-hoa8.jpg?w=220" />', 'http://vietnamnet.vn/vn/bat-dong-san/noi-that/306721/nhung-can-ho-chung-cu-dep-hut-hon-nho-ban-cong-hoa.html', '25/05/2016 (GMT+7)', 'http://imgs.vietnamnet.vn/Images/2016/05/25/14/20160525145810-ban-cong-hoa8.jpg', '', 'http://vietnamnet.vn/vn/bat-dong-san/noi-that/306721/nhung-can-ho-chung-cu-dep-hut-hon-nho-ban-cong-hoa.html', 1464170485, NULL),
(163, 'Hà Nội có thêm dự án sân golf gần 300ha', 'Ngày 23/05, UBND TP Hà Nội ban hành Thông báo số 138/TB-UBND về việc điều chỉnh danh mục sân golf trên địa bàn thành phố.<br /><img src="http://imgs.vietnamnet.vn/Images/2016/05/25/10/20160525102310-san-golf.jpg?w=220" />', 'http://vietnamnet.vn/vn/bat-dong-san/du-an/306624/ha-noi-co-them-du-an-san-golf-gan-300ha.html', '25/05/2016 (GMT+7)', 'http://imgs.vietnamnet.vn/Images/2016/05/25/10/20160525102310-san-golf.jpg', '', 'http://vietnamnet.vn/vn/bat-dong-san/du-an/306624/ha-noi-co-them-du-an-san-golf-gan-300ha.html', 1464170485, NULL),
(164, 'Những sân vườn khiến bạn đến rồi chẳng muốn về', 'Chỉ cần tưởng tượng đến việc được thả mình trong bầu không gian xanh mát, tách biệt hoàn toàn với ồn ào và khói bụi thì những sân vườn sau nhà chính là chốn thiên đường.<br /><img src="http://imgs.vietnamnet.vn/Images/2016/05/23/16/20160523160221-san-vuon1.jpg?w=220" />', 'http://vietnamnet.vn/vn/bat-dong-san/noi-that/306323/nhung-san-vuon-khien-ban-den-roi-chang-muon-ve.html', '25/05/2016 (GMT+7)', 'http://imgs.vietnamnet.vn/Images/2016/05/23/16/20160523160221-san-vuon1.jpg', '', 'http://vietnamnet.vn/vn/bat-dong-san/noi-that/306323/nhung-san-vuon-khien-ban-den-roi-chang-muon-ve.html', 1464170485, NULL),
